import * as constants from './constants';
//meeting function
function getAllMeetings() {
    return {
      type: constants.GET_ALL_MEETING,
    }
}

function addMeeting(meeting) {
    return {
        type: constants.ADD_MEETING,
        meeting,
    }
}

function deleteMeeting(id) {
    return {
        type: constants.DELETE_MEETING,
        id,
    }
}
//login function
function login(user) {
    return {
        type: constants.LOGIN,
        user,
    }
}
function logout() {
    return {
        type: constants.LOGOUT,
    }
}
/*Reducer*/
function refreshMeetings(meetings) {
    return {
        type: constants.REFRESH,
        meetings,
    }
}

function loginSuccess(userInfo) {
    return {
        type: constants.LOGIN_SUCCESS,
        username: userInfo.username,
        token: userInfo.token,
    }
}

function loginFail() {
    return {
        type: constants.LOGIN_FAIL,
    }
}

function changeInputSinceWhen(value) {
    return {
        type: constants.SINCEWHEN_CHANGE,
        value,
    }
}

function changeInputTilWhen(value) {
    return {
        type: constants.TILWHEN_CHANGE,
        value,
    }
}

function changeInputUser(value) {
    return {
        type: constants.INPUT_USER_CHANGE,
        value,
    }
}

function changeInputPassword(value) {
    return {
        type: constants.INPUT_PASSWORD_CHANGE,
        value,
    }
}

export default {
    getAllMeetings,
    addMeeting,
    deleteMeeting,
    login,
    refreshMeetings,
    loginSuccess,
    loginFail,
    logout,
    changeInputSinceWhen,
    changeInputTilWhen,
    changeInputUser,
    changeInputPassword,
};
